import './style.css';
import React from 'react';
import * as d3 from 'd3';
import * as d3s from 'd3-scale';
import wdscroll_bas from './wdscroll_bas.svg';

class Type extends React.Component {
    constructor(props) {
        super(props);
        this.map = {};
        this.next = this.next.bind(this);
        this.state = { data: []
            , detailsByCountry: []
            , indiceInit: 0
            }
    }
    
    componentDidMount() {
        var margin = {top: 350, right: 480, bottom: 350, left: 480};
        var radius = Math.min(margin.top, margin.right, margin.bottom, margin.left) - 10;
        var x = d3s.scale.linear().range([0, 2 * Math.PI]);
        var y = d3s.scale.pow().exponent(1.3).domain([0, 1]).range([0, radius]);
        var padding = 5;

        var hue = d3s.scale.category10();

        var luminance = d3s.scale.sqrt()
                .domain([0, 1e6])
                .clamp(true)
                .range([90, 20]);
        /* var svg = d3.select("body").append("svg")
         .attr("width", width)
         .attr("height", height)
         .append("g")
         .attr("transform", "translate(" + width / 2 + "," + (height / 2 + 10) + ")");
         var g = svg.selectAll("g")
         .data(partition.nodes(root))
         .enter().append("g");
         
         var path = g.append("path")
         .attr("d", arc)
         .style("fill", function(d) {
         return color((d.children ? d : d.parent).name);
         })
         .on("click", click);
         var text = g.append("text")
         .attr("x", function(d) {
         return y(d.y);
         })
         .attr("dx", "6") // margin
         .attr("dy", ".35em") // vertical-align
         .text(function(d) {
         return d.name;
         });*/

        var svg = d3.select("body").append("svg")
                .attr("width", margin.left + margin.right)
                .attr("height", margin.top + margin.bottom)
                .append("g")
                .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        /*
        var partition = d3.layout.partition()
                .sort(function(a, b) {
                    return d3.ascending(a.name, b.name);
                })
                .size([2 * Math.PI, radius]);

        */

        var partition = data => d3.partition()
        .size([2 * Math.PI, radius])(d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.value - a.value));

        var arc = d3.svg.arc()
                .startAngle(function(d) {
                    return d.x;
                })
                .endAngle(function(d) {
                    return d.x + d.dx - .01 / (d.depth + .2);
                })
                .innerRadius(function(d) {
                    return radius / 3 * d.depth;
                })
                .outerRadius(function(d) {
                    return radius / 3 * (d.depth + 1) - 1;
                });

        d3.json("./flare.json", function(error, root) {
            // Compute the initial layout on the entire tree to sum sizes.
            // Also compute the full name and fill color for each node,
            // and stash the children so they can be restored as we descend.
            partition
                    .value(function(d) {
                        return d.size;
                    })
                    .nodes(root)
                    .forEach(function(d) {
                        d._children = d.children;
                        d.sum = d.value;
                        d.key = this.key(d);
                        d.fill = this.fill(d);
                    });

            // Now redefine the value function to us e the previously-computed sum.
            partition
                    .children(function(d, depth) {

                        return depth < 2 ? d._children : null;
                    })
                    .value(function(d) {

                        return d.sum;
                    });

            var node = partition.nodes(root).slice(1);

            var center = svg.append("circle")
                    .attr("r", radius / 3)
                    .on("click", zoomOut);

            var abc = svg.selectAll(".arc")
                    .data(node)
                    .enter()
                    .append("g")
                    .attr("class", "arc");

            var path = abc.append("path")
                    .attr("d", arc)
                    .style("fill", function(d) {
                        return d.fill;
                    })
                    .each(function(d) {

                        this._current = updateArc(d);
                    })
                    .on("click", zoomIn);

            var textEnter = abc.append("text")
                    .style("fill-opacity", 1)
                    .style("fill", "white")

                    .attr("text-anchor", "middle")
                    .attr("dy", ".2em")
                    .attr("transform", function(d) {
                        var angle = x(d.x + d.dx / 2) * 180 / Math.PI - 90;
                        return "translate(" + (arc.centroid(d)) + ")rotate(" + (angle > 90 ? 0 : 0) + ")";
                    }).on("click", zoomIn);

            textEnter.append("tspan")
                    .attr("x", 0)
                    .text(function(d) {
                        return d.name;
                    });
            textEnter.append("tspan")
                    .attr("x", 0)
                    .attr("dy", "1em")
                    .text(function(d) {
                        return d.depth ? d.name.split(" ")[1] || "" : "";
                    });


            function zoomIn(p) {
                if (p.depth > 1)
                    p = p.parent;
                if (!p.children)
                    return;
                zoom(p, p);
            }

            function zoomOut(p) {
                if (!p.parent)
                    return;
                zoom(p.parent, p);
            }

            // Zoom to the specified new root.
            function zoom(root, p) {
                //textEnter.transition().attr("opacity", 0);
                if (document.documentElement.__transition__)
                    return;

                // Rescale outside angles to match the new layout.
                var enterArc,
                        exitArc,
                        outsideAngle = d3s.scale.linear().domain([0, 2 * Math.PI]);

                function insideArc(d) {
                    return p.key > d.key
                            ? {depth: d.depth - 1, x: 0, dx: 0} : p.key < d.key
                            ? {depth: d.depth - 1, x: 2 * Math.PI, dx: 0}
                    : {depth: 0, x: 0, dx: 2 * Math.PI};
                }

                function outsideArc(d) {
                    return {depth: d.depth + 1, x: outsideAngle(d.x), dx: outsideAngle(d.x + d.dx) - outsideAngle(d.x)};
                }

                center.datum(root);

                // When zooming in, arcs enter from the outside and exit to the inside.
                // Entering outside arcs start from the old layout.
                if (root === p)
                    enterArc = outsideArc, exitArc = insideArc, outsideAngle.range([p.x, p.x + p.dx]);

                path = path.data(partition.nodes(root).slice(1), function(d) {
                    return d.key;
                });
                textEnter = textEnter.data(partition.nodes(root).slice(1), function(d) {
                    
                    return d.key;
                });

                // When zooming out, arcs enter from the inside and exit to the outside.
                // Exiting outside arcs transition to the new layout.
                if (root !== p)
                    enterArc = insideArc, exitArc = outsideArc, outsideAngle.range([p.x, p.x + p.dx]);

                d3.transition().duration(d3.event.altKey ? 7500 : 750).each(function() {

                
                    path.exit().transition()
                            .style("fill-opacity", function(d) {

                                return d.depth === 1 + (root === p) ? 1 : 0;
                            })
                            .attrTween("d", function(d) {
                                return arcTween.call(this, exitArc(d));
                            })
                            .remove();
                   
                    d3.selectAll("text").remove();
                   
                    //d3.selectAll("g").remove();
                    /*svg = d3.select("body").append("svg")
                     .attr("width", margin.left + margin.right)
                     .attr("height", margin.top + margin.bottom)
                     .append("g")
                     .attr("transform", "translate(" + margin.left + "," + margin.top + ")");
                     abc = svg.selectAll(".arc")
                     .data(node)
                     .enter()
                     .append("g")
                     .attr("class", "arc");*/
                    // d3.transition().attr("opacity", 0);
                    // textEnter.remove();


                   path.enter().append("path")
                            .style("fill-opacity", function(d) {
                                return d.depth === 2 - (root === p) ? 1 : 0;
                            })
                            .style("fill", function(d) {
                                return d.fill;
                            })
                            .on("click", zoomIn)
                            .each(function(d) {
                                this._current = enterArc(d);
                            });

                    textEnter.enter().append("text")               
                            .style("fill", "white")
                            .attr("transform", function(d) {
                                var angle = x(d.x + d.dx / 2) * 180 / Math.PI - 90;
                                return "translate(" + (arc.centroid(d)) + ")rotate(" + (angle > 90 ? 0 : 0) + ")";
                            }).attr("text-anchor", "middle")
                            .text(function(d) {
                                return d.name;
                            }).on("click", zoomIn);
                            
                    path.transition()
                            .style("fill-opacity", 1)
                            .attrTween("d", function(d) {
                                return arcTween.call(this, updateArc(d));
                            });
                });
            }
        });

        function colour(d) {
            if (d.children) {
                // There is a maximum of two children!
                var colours = d.children.map(colour),
                        a = d3.hsl(colours[0]),
                        b = d3.hsl(colours[1]);
                // L*a*b* might be better here...
                return d3.hsl((a.h + b.h) / 2, a.s * 1.2, a.l / 1.2);
            }
            return d.colour || "#fff";
        }

        function key(d) {
            var k = [], p = d;
            while (p.depth)
                k.push(p.name), p = p.parent;
            return k.reverse().join(".");
        }

        function fill(d) {
            var p = d;
            while (p.depth > 1)
                p = p.parent;
            var c = d3.lab(hue(p.name));
            c.l = luminance(d.sum);
            return c;
        }

        function arcTween(b) {
            var i = d3.interpolate(this._current, b);
            this._current = i(0);
            return function(t) {
                return arc(i(t));
            };
        }

        function updateArc(d) {
            return {depth: d.depth, x: d.x, dx: d.dx};
        }
        d3.select(self.frameElement).style("height", margin.top + margin.bottom + "px");

    }
    
    next() {
       
    }

    render() {
    return <div id="Typo">
        <p className="arrow-top"><a href="#section1"><img src={wdscroll_bas} alt="arrow_top" /></a></p>
        <p className="arrow-right"><a href="#section-mapping"><img src={wdscroll_bas} alt="arrow_right" /></a></p>
    </div>
  }
}

export default Map;
