// unfinished/src/components/details.jsx
import React        from 'react';
//import d3           from 'd3';

let j = -1;
let allInitDetails = [];
let max = 0;

function getDetailsKey(max3) {
    if (j <= max3){
        return j++;
    } else {
        return 0;
    }
}

function fillDetails(allInitDetails2, max2){
    j = getDetailsKey(max2);
    let details = allInitDetails2.data[j];
    if (typeof(details) == 'undefined'){
        return <div className="details" id="section-map">
            <p className="details-title">#Fake News dans le monde</p> 
            <p><img src="https://infographic.statista.com/normal/chartoftheday_14565_l_exposition_aux_fake_news_dans_le_monde_n.jpg" alt="initiative" /></p>
            <p>Et face à ce constat quelles initiatives citoyennes?</p> 
        </div>
    } // 
    return <div>
                <div className="details" id="section-map">
                    <p className="details-logo"><img src={details.logo} alt=""/></p>
                    <p className="details-title">#{details.title}</p> 
                    <p>Type {details.type}</p>
                    <p>When {details.when}</p> 
                    <p>Where {details.where}</p>
                    <p>Who {details.who}</p>  
                    <p>URL <a href={details.url} target="_blank" rel="noopener noreferrer">www</a></p>
                    <p><img src={details.img} alt="initiative" /></p>
                    <p>What {details.what}</p>
                </div>
                <div id="next">
                    <input type="button" value = "next" onClick={next(allInitDetails, max, j)} id = "next" />
                </div>
            </div>
}
function getDetailsContent(details) {
    return <div className="details" id="section-map">
            <p className="details-logo"><img src={details.logo} alt=""/></p>
            <p className="details-title">#{details.title}</p> 
            <p>Type {details.type}</p>
            <p>When {details.when}</p> 
            <p>Where {details.where}</p>
            <p>Who {details.who}</p>  
            <p>URL <a href={details.url} target="_blank" rel="noopener noreferrer">www</a></p>
            <p><img src={details.img} alt="initiative" /></p>
            <p>What {details.what}</p>
        </div>
}
function getNext(allInitDetailsProp, maxProp, jProp) {
    return <div id="next">
                <input type="button" value = "next" onClick={next(allInitDetailsProp, maxProp, jProp)} id = "next" />
            </div>
}
function next (allInitDetailsProp, maxProp, jProp){
    var sectionMap = document.getElementById("section-map");
    let details = allInitDetails.data[jProp];
    let newDetails = getDetailsContent(details);
    let newNext = getNext(allInitDetailsProp, maxProp, jProp+1);
    sectionMap.innerHTML = newDetails + newNext;
}

function PopupTemplate(props){
    console.log("props.data : " + props.data);
    allInitDetails = props;
    max = allInitDetails.length;
    var divDetails = fillDetails(allInitDetails, max);   
    return <div>
                {divDetails}
            </div>

          
}

export default (props) => {
    console.log('props' + props);
    return PopupTemplate(props)
}
