import './style.css';
import React from 'react';
import * as d3 from 'd3';
import wdscroll_bas from './wdscroll_bas.svg';

class Type extends React.Component {
    constructor(props) {
        super(props);
        this.map = {};
        this.next = this.next.bind(this);
        this.state = { data: []
            , detailsByCountry: []
            , indiceInit: 0
            }
    }
    
    componentDidMount() {
        let that = this;
        const root = partition(data);
        
        const svg = d3.select('#type').append("svg")
        .attr("id", "svg")
        .style("width", "100%")
        .style("height", "auto")
        .attr("class", "svg")
        .style("box-sizing", "border-box");
            
        const g = svg.append("g");

        g.append("g")
            .attr("fill-opacity", 0.6)
            .selectAll("path")
            .data(root.descendants().filter(d => d.depth))
            .enter().append("path")
            .attr("fill", d => { while (d.depth > 1) d = d.parent; return color(d.data.name); })
            .attr("d", arc)
            .append("title")
            .text(d => `${d.ancestors().map(d => d.data.name).reverse().join("/")}\n${format(d.value)}`);

        g.append("g")
            .attr("pointer-events", "none")
            .attr("text-anchor", "middle")
            .selectAll("text")
            .data(root.descendants().filter(d => d.depth && (d.y0 + d.y1) / 2 * (d.x1 - d.x0) > 10))
            .enter().append("text")
            .attr("transform", function(d) {
                const x = (d.x0 + d.x1) / 2 * 180 / Math.PI;
                const y = (d.y0 + d.y1) / 2;
                return `rotate(${x - 90}) translate(${y},0) rotate(${x < 180 ? 0 : 180})`;
            })
            .attr("dy", "0.35em")
            .text(d => d.data.name);

        document.body.appendChild(svg.node());

        const box = g.node().getBBox();

        svg.remove()
            .attr("width", box.width)
            .attr("height", box.height)
            .attr("viewBox", `${box.x} ${box.y} ${box.width} ${box.height}`);

        var data = require("./flare");
        
        var partition = data => d3.partition()
            .size([2 * Math.PI, radius])
        (d3.hierarchy(data)
            .sum(d => d.size)
            .sort((a, b) => b.value - a.value));
        
        var color = d3.scaleOrdinal().range(d3.quantize(d3.interpolateRainbow, data.children.length + 1));

        // Breadcrumb dimensions: width, height, spacing, width of tip/tail.
        var b = {
            w: 75, h: 30, s: 3, t: 10
        };
        
        // Mapping of step names to colors.

        let format = d3.format(",d");

        let width = 932;

        let radius = width / 2;

        var arc = d3.arc()
            .startAngle(d => d.x0)
            .endAngle(d => d.x1)
            .padAngle(d => Math.min((d.x1 - d.x0) / 2, 0.005))
            .padRadius(radius / 2)
            .innerRadius(d => d.y0)
            .outerRadius(d => d.y1 - 1);

    }
    
    next() {
       
    }

    render() {
    return <div id="Typo">
        <p className="arrow-top"><a href="#section1"><img src={wdscroll_bas} alt="arrow_top" /></a></p>
        <p className="arrow-right"><a href="#section-mapping"><img src={wdscroll_bas} alt="arrow_right" /></a></p>
    </div>
  }
}

export default Map;
