/*

import './style.css';
import React from 'react';
import * as d3 from 'd3';
import wdscroll_bas from './wdscroll_bas.svg';
*/
'use strict';

const React = require('react');
const d3 = require('d3');

/*
function renderArticles(articles) {
    if (articles.length > 0) {      
        return articles.map((article, index) => (
            <articles key={index} article={article} />
        ));
    }
    else return [];
}
*/
function renderArticles(chart) {
    if (chart.length > 0) {      
        return <chart />
    }
    else return [];
}
/*
const Article = ({article}) => {
    return ( 
        <article key={article.id}>
            <a href={article.link}>{article.title}</a>
            <p>{article.description}</p>
        </article>
    );
};
*/

const chartTypo = () => {
    const url = "./flare.json";
    d3.json(url, function (data) {
        console.log(data);
    });

    const partition = data => d3.partition()
        .size([2 * Math.PI, radius])
    (d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.value - a.value));
    const color = d3.scaleOrdinal().range(d3.quantize(d3.interpolateRainbow, this.data.children.length + 1));
    const format = d3.format(",d");
    const width = 932;
    const radius = width / 2;
    const arc = d3.arc()
        .startAngle(d => d.x0)
        .endAngle(d => d.x1)
        .padAngle(d => Math.min((d.x1 - d.x0) / 2, 0.005))
        .padRadius(radius / 2)
        .innerRadius(d => d.y0)
        .outerRadius(d => d.y1 - 1);
        
    const root = this.partition(this.data);      
    const svg = d3.select("#type")
        .style("width", "100%")
        .style("height", "auto")
        .style("padding", "10px")
        .style("font", "10px sans-serif")
        .style("box-sizing", "border-box");
      
    const g = svg.append("g");
    
    g.append("g")
        .attr("fill-opacity", 0.6)
      .selectAll("path")
      .data(root.descendants().filter(d => d.depth))
      .enter().append("path")
        .attr("fill", d => { while (d.depth > 1) d = d.parent; return this.color(d.data.name); })
        .attr("d", this.arc)
      .append("title")
        .text(d => `${d.ancestors().map(d => d.data.name).reverse().join("/")}\n${this.format(d.value)}`);
  
    g.append("g")
        .attr("pointer-events", "none")
        .attr("text-anchor", "middle")
      .selectAll("text")
      .data(root.descendants().filter(d => d.depth && (d.y0 + d.y1) / 2 * (d.x1 - d.x0) > 10))
      .enter().append("text")
        .attr("transform", function(d) {
          const x = (d.x0 + d.x1) / 2 * 180 / Math.PI;
          const y = (d.y0 + d.y1) / 2;
          return `rotate(${x - 90}) translate(${y},0) rotate(${x < 180 ? 0 : 180})`;
        })
        .attr("dy", "0.35em")
        .text(d => d.data.name);
  
    document.body.appendChild(svg.node());
  
    const box = g.node().getBBox();
  
    svg.remove()
        .attr("width", box.width)
        .attr("height", box.height)
        .attr("viewBox", `${box.x} ${box.y} ${box.width} ${box.height}`);
  
    return svg.node();
  };

//const Article = React.createClass({
const chart = React.createClass({
    render() {
        //const articles = renderArticles(this.props.articles);
        //const chart = renderArticles(this.props.chart);
        // Entre section  { articles }
        return (
            <section>
                { chartTypo }
            </section>
        );
    }
});
// module.exports = Article;
module.exports = chart;
