//import './style.css';
import React from 'react';
import * as d3 from 'd3';

class Mapmonde extends React.Component {
    constructor(props) {
        super(props);
        this.map = {};
        this.state = { data: {} }
    }
    
    componentDidMount() {
        const width = document.getElementById("container2").offsetWidth * 0.95,
            height = 500,
            legendCellSize = 20,
            colors = ['#d4eac7', '#c6e3b5', '#b7dda2', '#a9d68f', '#9bcf7d', '#8cc86a', '#7ec157', '#77be4e', '#70ba45', '#65a83e', '#599537', '#4e8230', '#437029', '#385d22', '#2d4a1c', '#223815'];

        const svg = d3.select('#mapmonde').append("svg")
            .attr("id", "svg")
            .attr("width", width)
            .attr("height", height)
            .attr("class", "svg");
        
        var legend = svg.append('g')
            .attr('transform', 'translate(40, 50)');
        
    }
render() {
    return <div id="Mapmonde"></div>
  }
}

export default Mapmonde;
