//import './style.css';
import React from 'react';
import Datamap from '../../../node_modules/datamaps/dist/datamaps.world.min.js';
import * as d3 from 'd3';

class Tuto extends React.Component {
    constructor(props) {
        super(props);
        this.map = {};
        this.state = { data: {} }
    }
    
    componentDidMount() {
        const width = 700, height = 550;

        const path = d3.geoPath();
        
        const projection = d3.geoConicConformal() // Lambert-93
            .center([2.454071, 46.279229]) // Center on France
            .scale(2600)
            .translate([width / 2 - 50, height / 2]);
        
        path.projection(projection);
        
        const svg = d3.select('#tutoMap').append("svg")
            .attr("id", "svg")
            .attr("width", width)
            .attr("height", height)
            .attr("class", "Blues");
        
        const deps = svg.append("g");
    }
render() {
    return <div id="tutoMap"></div>
  }
}

export default Tuto;
